CREATE TABLE IF NOT EXISTS "Users" ("id"  BIGSERIAL  , "email" VARCHAR(255) NOT NULL , "password" TEXT NOT NULL , "passwordSalt" TEXT NOT NULL , "name" VARCHAR(100) , "userType" "public"."enum_Users_userType" NOT NULL DEFAULT 'USER' , "createdAt" TIMESTAMP WITH TIME ZONE NOT NULL, "updatedAt" TIMESTAMP WITH TIME ZONE NOT NULL, PRIMARY KEY ("id")); COMMENT ON COLUMN "Users"."id" IS '식별자'; COMMENT ON COLUMN "Users"."email" IS '이메일(아이디)'; COMMENT ON COLUMN "Users"."password" IS '해싱된 패스워드'; COMMENT ON COLUMN "Users"."passwordSalt" IS '패스워드 솔팅값'; COMMENT ON COLUMN "Users"."name" IS '사용자 이름'; COMMENT ON COLUMN "Users"."userType" IS '사용자 타입';